package com.vanness.kotlin

fun isPrime(x: Int, i: Int = 2): Boolean {
    if (x < 2)
        return false
    if (i * i > x)
        return true
    if (x % i == 0)
        return false
    return isPrime(x, i + 1)
}


fun main() {
    println("Masukkan Jumlah Case yang ingin dimasukkan :")
    val t = readLine()!!.toInt()
    repeat(t) {
        println("Masukkan Angka : ")
        val x = readLine()!!.toInt()
        val result = if (isPrime(x)) "Prime Number" else "Not Prime Number"
        println("Case #${it+1}: $result")
        }
}